public class GetOneMagasin : ICarterModule
{
    public void AddRoutes(IEndpointRouteBuilder app)
    {
        app.MapGet("/magasin/{BoutiqueId}/{id}", async (Guid BoutiqueId, Guid id, ISender sender) =>
        {
            var query = new GetOneMagasinQuery(id);

            var result = await sender.Send(query);

            var response = result.Adapt<GetOneMagasinResponse>();
            var baseResponse = ResponseFactory.Success(response, "Magasin r√©cup√©r√© avec succ√®s", StatusCodes.Status200OK);

            return Results.Ok(baseResponse);
        })
       .WithName("GetOneMagasin")
       .WithTags("Magasin")
       .Produces<BaseResponse<GetOneMagasinResponse>>(StatusCodes.Status200OK)
